import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";


const GetUserQuestions = () => {
    const navigate = useNavigate();
    const[newQuestion,setNewQuestion] = useState('')

    async function fetchUserSpecificQuestions(){
        try{
            // get token from client side storage
            const token = localStorage.getItem("token");

            if (!token) {
                navigate("/login");
                return;
              }

        // send a fetch request to the api endpoint for questions
        const req = await fetch("http://localhost:1337/api", {
            method: "GET",
            headers:{
                "x-access-token":token
            }
        });

        const data = await req.json();

        if(data.status === 'ok'){
            setNewQuestion(data.question);
        }else{
            console.log(data.error);
        }
        }catch (error) {
            console.error(error);
            alert("An error occurred while fetching the questions.");
          }
    }

    useEffect(()=>{
        if(newQuestion){
            fetchUserSpecificQuestions();
        }
    },[newQuestion])

        return (
            <ul>
              {newQuestion && newQuestion.length > 0 ? (
                newQuestion.map((addedQuestion) => (
                  <div key={addedQuestion._id}>
                    <li>{addedQuestion.question}</li>
                  </div>
                ))
              ) : (
                <p>No questions found</p>
              )}
            </ul>
          );


}

export default GetUserQuestions;
