import React, { useState, useEffect } from "react";

const GetProfile = () => {
  const [profile, setProfile] = useState("");
  const [loading, setLoading] = useState(true);
  const [fileInputKey, setFileInputKey] = useState(0); // Key to reset the input field

  // Fetch profile from the server
  async function getProfile() {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch("http://localhost:1337/api/getProfile", {
        method: "GET",
        headers: {
          "x-access-token": token,
        },
      });

      const data = await response.json();

      if (data.status === "ok") {
        setLoading(false);
        setProfile("http://localhost:1337/" + data.profile);
      } else {
        alert(data.error);
      }
    } catch (error) {
      console.error("An error occurred while fetching the profile:", error);
      alert("An error occurred while fetching the profile. Please check the console for details.");
    }
  }

  // Function to handle file input change
  function handleFileInputChange() {
    // Increment the key to reset the input field
    setFileInputKey((prevKey) => prevKey + 1);

    // Fetch the updated profile after resetting the input
    getProfile();
  }

  // Execute the getProfile function when the component mounts
  useEffect(() => {
    getProfile();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <img
            src={profile}
            alt="User Profile"
            style={{ height: 100, width: 200 }}
          />
          <input
            key={fileInputKey}
            type="file"
            onChange={handleFileInputChange}
          />
        </div>
      )}
    </div>
  );
};

export default GetProfile;
