import React, {useState} from "react";
import { Home } from "lucide-react";
import { Bell } from "lucide-react";
import { Search } from "lucide-react";
import { PenSquare } from "lucide-react";
import "./AppBar.css";
import QuestionModal from "../Home/Pages/Question/UserQuestions";

const AppBar = (props) => {
  const [isModalOpen,setIsModalOpen] = useState(false);
  const [questions, setQuestions] = useState([]);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const submitQuestion = (question) => {
    setQuestions([...questions, question]);
  };
  return (
    <div>
      <div className="AppBar">
        <div className="right-container">
          <div className="appbar-items">
            <Home strokeWidth={1} color="black" style={{ opacity: 0.5 }}/>
          </div>
          <div className="appbar-items">
            <Bell strokeWidth={1} color="black" style={{ opacity: 0.5 }}/>
          </div>
          <button onClick={openModal}>
        <PenSquare strokeWidth={1} color="black" style={{ opacity: 0.5 }} />
      </button>

      <QuestionModal
        isOpen={isModalOpen}
        onClose={closeModal}
        onSubmit={submitQuestion}
      />
      <div>
        {QuestionModal.map((q,index)=>{
          <></>
        })}
      </div>
        </div>
        <div className="input-container">
          <Search strokeWidth={2} style={{ opacity: 0.3 }} />
          <input></input>
        </div>
      </div>
    </div>
  );
};

export default AppBar;
