import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UserQuestions.css";
import jwt from "jsonwebtoken";


const UserQuestions = ({ isOpen, onClose, onSubmit }) => {
  const [question, setQuestion] = useState("");
  const navigate = useNavigate();

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = () => {
    onSubmit(question);
    setQuestion("");
    onClose();
  };

  // post request to add the question to the database
  async function postQuestions(){
    try{
      const token = localStorage.getItem('token');
      if(!token){
        navigate('/login')
        console.log("token does not exist");
        return
      }
      const user = jwt.decode(token);

      if (!user) {
        localStorage.removeItem("token");
        navigate("/login");
        return;
      }

      // send a fetch request to the api endPoint
      const req = await fetch("http://localhost:1337/api/addQuestions")


    }catch(error){

    }
  }

  return (
    isOpen && (
      <div className="full-screen-modal">
        <div className="modal-content">

          <textarea
            placeholder="Ask your question..."
            value={question}
            onChange={handleQuestionChange}
          />
          <button className="submit-button" onClick={handleSubmit}>Submit</button>
          <button className='close-button ' onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    )
  );
};

export default UserQuestions;
