import React, { useEffect, useState } from "react";
import { UserProfileImage } from "./ProfileImageContext";

const SetProfile = () => {
  const { profileImage, setProfileImage } = UserProfileImage();
  const [isEditing, setIsEditing] = useState(false);

  // send fetch request to post the profile picture
  async function fectchProfile() {
    // ... your existing code for fetching and updating the profile

    // After updating, set isEditing back to false to hide the input
    setIsEditing(false);
  }

  function HandleImageChange(e) {
    setProfileImage(e.target.files[0]);
  }

  return (
    <div>
      {isEditing ? (
        <div>
          <input type="file" onChange={HandleImageChange} />
          <button onClick={fectchProfile}>Upload</button>
        </div>
      ) : (
        <button onClick={() => setIsEditing(true)}>Edit Profile</button>
      )}
    </div>
  );
};

export default SetProfile;
