import React, { useState } from "react";
import { UserProfileImage } from "./ProfileImageContext";

const SetProfile = () => {
  const { profileImage, setProfileImage } = UserProfileImage();
  const [imageSelected, setImageSelected] = useState(false);
  const [loading, setLoading] = useState(false);

  // Send the fetch request to upload the profile picture
  async function fetchProfile() {
    if (!profileImage) {
      alert("Please select an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("image", profileImage);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Token not found");
        return;
      }

      setLoading(true);

      // Post request to upload the image to the server
      const response = await fetch("http://localhost:1337/api/setProfile", {
        method: "POST",
        headers: {
          "x-access-token": token,
        },
        body: formData,
      });

      const data = await response.json();
      console.log("Server Response:", data);

      if (data.status === "ok") {
        console.log("Image saved in the database");
        setProfileImage("http://localhost:1337/" + data.profile);
        setLoading(false);
      }
    } catch (error) {
      console.error("Error:", error);
      setLoading(false);
    }
  }

  function HandleImageChange(e) {
    setProfileImage(e.target.files[0]);
    setImageSelected(true);
  }

  return (
    <div>
      <input type="file" onChange={HandleImageChange} />
      <button onClick={fetchProfile}>Upload</button>
      {loading && <p>Uploading...</p>}
      {imageSelected && (
        <img
          src={profileImage}
          alt="User Profile"
          style={{ height: 100, width: 200 }}
        />
      )}
    </div>
  );
};

export default SetProfile;
