import React from "react";
import { useNavigate } from 'react-router-dom';

import styled from "styled-components";
import AppBar from "../CommonComponents/AppBar"
import backgroundImage from "../Assets/mario.png"; 



const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh; 
  width: 100vw; 
  background:  #eaf6f6; ;
  background-image: url(${backgroundImage});
  background-size:cover;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  flex-grow: 1; /* Allow content to grow and take up available space */
`;

const StyledInputDiv = styled.div`
  position: relative; 
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 20px; 

  & input{
    margin-bottom: 10px;
    width: 500px;
    height: 50px;
    font-size:15px;
    border-radius:10px;
    border: 5px solid ;  
    padding-left:20px;
  }
`;

const StyledButton = styled.div`
width:150px;
height:30px;
color:white;
background-color: rgb(89, 4, 4);
font-size:20px;
font-weight:bold;
padding-left:60px;
padding-top:7px;
margin-top:20px;
border-radius:20px;
border: 5px solid ;  
`

const Signup = () => {
  const history = useNavigate();
  const handleSignUp = () =>{
    h('/login');
  };

  return (
   <Container>
    <AppBar>
      <label>Sign Up</label></AppBar>
    <Content>
      <StyledInputDiv>
        <input type="text" id="email" placeholder="Email" />
      </StyledInputDiv>
      <StyledInputDiv>
        <input type="password" id="password" placeholder="create password" />
      </StyledInputDiv>
      <StyledInputDiv>
        <input type="password" id="password" placeholder="confirm password" />
      </StyledInputDiv>

      <StyledButton onClick={handleSignUp}>Sign Up</StyledButton>
      </Content>
      </Container>
  );
};

export default Signup;
