import React from "react";
import { Home } from "lucide-react";
import { Bell } from "lucide-react";
import { Search } from "lucide-react";
import { PenSquare } from "lucide-react";
import "./AppBar.css";

const AppBar = (props) => {
  const toggleMenu = () => {
    const appBar = document.querySelector(".AppBar");
    if (appBar.style.display === "block") {
      appBar.style.display = "none";
    } else {
      appBar.style.display = "block";
    }
  };
  return (
    <div>
      <div className="AppBar">
        <div className="right-container">
          <div className="appbar-items">
            <Home strokeWidth={1} color="black" style={{ opacity: 0.7 }}/>
          </div>
          <div className="appbar-items">
            <Bell strokeWidth={1} color="black" style={{ opacity: 0.7 }}/>
          </div>
          <div className="appbar-items">
            <PenSquare strokeWidth={1} color="black" style={{ opacity: 0.3 }} />
          </div>
        </div>
        <div className="input-container">
          <Search strokeWidth={2} style={{ opacity: 0.3 }} />
          <input></input>
        </div>
      </div>
    </div>
  );
};

export default AppBar;
