import React, { useState, useEffect } from "react";

const GetProfile = async () => {
  const [profile, setProfile] = useState("");

  // fetch profile from the server
  async function getProfile() {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch("http://localhost:1337/api/getProfile", {
        method: "GET",
        headers: {
          "x-access-token": token,
        },
      });
      const data = response.json();
      if (data.status === "ok") {
        setProfile("http://localhost:1337/" + data.profile);
      } else {
        alert(data.error);
      }
    } catch (error) {
      console.error("An error occurred while fetching the profile:", error);
      alert(
        "An error occurred while fetching the profile. Please check the console for details."
      );
    }
  }
//   side
  useEffect(() => {
    getProfile();
  }, []);
  return (
    <div>
      <img
        src={profile}
        alt="profilePic"
        style={{ height: 100, width: 200 }}
      ></img>
    </div>
  );
};

export default GetProfile;
