import React, { useState } from "react";
import { Home, Bell, Search, PenSquare } from "lucide-react";
import "./AppBar.css";

const AppBar = (props) => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className={`AppBar ${menuOpen ? "mobile" : ""}`}>
      <div className="right-container">
        <div className={`appbar-items ${menuOpen ? "vertical" : ""}`}>
          <Home strokeWidth={1} />
        </div>
        <div className={`appbar-items ${menuOpen ? "vertical" : ""}`}>
          <Bell strokeWidth={1} />
        </div>
        <div className={`appbar-items ${menuOpen ? "vertical" : ""}`}>
          <PenSquare strokeWidth={1} />
        </div>
      </div>
      <div className="input-container">
        <Search
          strokeWidth={2}
          style={{ opacity: menuOpen ? 0 : 0.3 }}
        />
        <input style={{ display: menuOpen ? "none" : "block" }} />
      </div>
      <button className="burger-button" onClick={toggleMenu}>
        <div className={`bar ${menuOpen ? "open" : ""}`} />
        <div className={`bar ${menuOpen ? "open" : ""}`} />
        <div className={`bar ${menuOpen ? "open" : ""}`} />
      </button>
    </div>
  );
};

export default AppBar;
