import React, { useState } from "react";
import styled from "styled-components";
import AppBar from "../CommonComponents/AppBar";
import backgroundImage from "../Assets/mario_login.png"; // Import the image using require

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  width: 100vw;
  background: #eaf6f6;
  background-image: url(${backgroundImage});
  background-size: cover;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-bottom: 80px;
  flex-grow: 1; /* Allow content to grow and take up available space */
`;

const StyledInputDiv = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 20px;

  & input {
    margin-bottom: 10px;
    width: 500px;
    height: 50px;
    font-size: 15px;
    border-radius: 10px;
    border: 5px solid;
    padding-left: 20px;
  }
`;

const StyledButton = styled.div`
  width: 150px;
  height: 30px;
  color: white;
  background-color: rgb(89, 4, 4);
  font-size: 20px;
  font-weight: bold;
  padding-left: 90px;
  padding-top: 7px;
  margin-top: 20px;
  border-radius: 20px;
  border: 5px solid;
`;

const Login = () => {

  const [email,setEmail] = useState('');


  return (
    <Container>
      <AppBar>
        <label style={{ paddingRight: 30 }}>Login</label>
      </AppBar>
      <Content>
        <StyledInputDiv>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
          />
        </StyledInputDiv>
        <StyledInputDiv>
          <input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            placeholder="create password"
          />
        </StyledInputDiv>
        <StyledButton>Login</StyledButton>
      </Content>
    </Container>
  );
};

export default Login;
