import React, { useEffect, useState } from "react";
import "./EditUserQuestion.css";
import { baseApiUrl } from "../apiConfig";
import { useNavigate } from "react-router-dom";

const EditUserQuestion = ({ questionId, onClose, isEditOpen }) => {
  const navigate = useNavigate(); // Fix: useNavigate instead of Navigate
  const [questionData, setQuestionData] = useState(null);

  // Function to fetch edit question from the server
  async function getEditModal() {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        navigate("/login"); // Fix: use navigate instead of Navigate
        return;
      }

      // send a fetch request from the api to the server for getting the question to change
      const response = await fetch(`${baseApiUrl}getEditQuestion`, {
        method: "GET",
        headers: {
          "x-access-token": token,
        },
      });

      const fetchedData = await response.json();

      if (fetchedData.status === "ok") {
        setQuestionData(fetchedData); // Fix: set the question data in state
      } else {
        console.error(fetchedData.error); // Log error if status is not ok
      }
    } catch (error) {
      console.error("Error fetching edit question:", error);
    }
  }

  useEffect(() => {
    if (isEditOpen) {
      getEditModal();
    }
  }, [isEditOpen]); // Fix: add isEditOpen as a dependency

  return (
    isEditOpen && (
      <div className="full-screen-modal">
        <div className="modal-content">
          <h1>Edit Question</h1>
          <p>Question ID: {questionId}</p>
          <textarea placeholder="Ask your question..." />
          <button className="close-button" onClick={onClose}>
            Close
          </button>
          {/* Render additional elements as needed */}
          {/* For example, a submit button */}
          {/* <button className="submit-button">Submit</button> */}
        </div>
      </div>
    )
  );
};

export default EditUserQuestion;
