import React, { createContext, useContext, useState } from 'react';


const EditIdContext = createContext({ editId: null, setEditId: () => {} });

export const useEditId = () => {
    return useContext(EditIdContext);
}

export const EditIdProvider = ({ children }) => {
    const [editId, setEditId] = useState(null);

    return (
        <EditIdContext.Provider value={{ editId, setEditId }}>
            {children}
        </EditIdContext.Provider>
    );
}


export default EditIdContext;
