import React, { useState, useEffect } from "react";

const GetProfile = () => {
  const [profile, setProfile] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch profile from the server
  async function getProfile() {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch("http://localhost:1337/api/getProfile", {
        method: "GET",
        headers: {
          "x-access-token": token,
        },
      });
      const data = await response.json();
      if (data.status === "ok") {
        setProfile("http://localhost:1337/" + data.profile);
      } else {
        alert(data.error);
      }
    } catch (error) {
      console.error("An error occurred while fetching the profile:", error);
      alert("An error occurred while fetching the profile. Please check the console for details.");
    } finally {
      setLoading(false);
    }
  }

  // Side effect to execute the getProfile function
  useEffect(() => {
    getProfile();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <img src={profile} alt="User Profile" style={{ height: 100, width: 200 }} />
      )}
    </div>
  );
};

export default GetProfile;
