import React, { createContext, useContext, useState } from 'react';

const EditIdContext = createContext();

export function useEditId() {
  const context = useContext(EditIdContext) || { editId: null, setEditId: () => {} };
  return context;
}

export function EditIdProvider({ children }) {
  const [editId, setEditId] = useState(''); // Set an initial value other than null
  return (
    <EditIdContext.Provider value={{ editId, setEditId }}>
      {children}
    </EditIdContext.Provider>
  );
}
