import React, { useEffect, useState } from "react";
import { UserProfileImage } from "./ProfileImageContext";

const SetProfile = () => {
  const { profileImage, setProfileImage } = UserProfileImage();
  const [isEditing, setIsEditing] = useState(false);

  // send fetch request to post the profile picture
  async function fectchProfile() {
    if (!profileImage) {
      // Don't make a request if no file is selected
      return;
    }
    const formData = new FormData();
    formData.append("image", profileImage);
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("token not found");
        return;
      }
      // Post request to upload the image to the database and save temporarily on the server
      const response = await fetch("http://localhost:1337/api/setProfile", {
        method: "POST",
        headers: {
          "x-access-token": token,
        },
        body: formData,
      });
      const data = await response.json();
      console.log("Server Response:", data);
      if (data.status === "ok") {
        console.log("Image saved in the database");
      }
    } catch {
      console.error("Error:");
    }
  }

  function handleUpload() {
    if (isEditing) {
      fectchProfile();
      setIsEditing(false);
    } else {
      setIsEditing(true);
    }
  }

  function HandleImageChange(e) {
    setProfileImage(e.target.files[0]);
  }

  return (
    <div>
      {isEditing ? (
        <div>
          <input type="file" onChange={HandleImageChange} />
          <button onClick={handleUpload}>Upload</button>
        </div>
      ) : (
        <button onClick={handleUpload}>Edit Profile</button>
      )}
    </div>
  );
};

export default SetProfile;
